import styles from "./testinggrounds.module.css";
import React, { useState } from "react";
import OutlineButton from "./components/outlinebutton/outlinebutton";
import TestButton from "./components/testbutton/testbutton";

export default function TestingGrounds() {
  const [toggle, setToggle] = useState(false); // toggle is the state variable, so if its true, something will happen, if its false, something else will happen
  // setToggle is the function we use to update the state of our component
  //useState is the hook, when we pass false as an argument React uses that as the initial state
  const age = 15;
  const isBig = true;
  const list = ["Arjun", "Karam", "Karmvir"];
  const [moneyArray, setMoneyArray] = useState([]);

  const handleClick = (e) => {
    var Xlocation = e.clientX;
    var Ylocation = e.clientY;

    var newElement = (
      <div className={styles.money} style={{ top: Ylocation, left: Xlocation }}>
        $1.00
      </div>
    );
    setMoneyArray([...moneyArray, newElement]);
  };

  return (
    <>
      <div>
        <div style={{ fontSize: isBig ? "30px" : "10px" }}> You are: </div>
        {age >= 18 ? <h1>over aged</h1> : <h1>underaged</h1>}

        <OutlineButton title="Toggle Text" click={() => setToggle(!toggle)} />
        {toggle && <div> if this works then something went well </div> // If toggle is true, then render the div
        }

        <div>
          {list.map((name, key) => {
            // Key is the index of each element, Name is what is contained (i.e, the 2nd value is "Karam")
            return (
              <h1>
                Username {key}: {name}
              </h1>
            );
          })}
        </div>

        <OutlineButton title="click me" click={handleClick} />
      </div>
      <TestButton click={handleClick}></TestButton>
      <div className={styles.money}>$3</div>
      <div>{moneyArray}</div>
    </>
  );
}
