import styles from "./clickupgradeslist.module.css";
import 'react-tooltip/dist/react-tooltip.css'
import Tippy from '@tippyjs/react';
import 'tippy.js/dist/tippy.css';
import React from 'react';

const ClickUpgradesList = ({ multiplierFunction, frontendArray, moneyFunction, balance, ...props }) => {

    const clickCheck = (e, price, multiplier) => {
        var Xlocation = e.clientX;
        var Ylocation = e.clientY;
        if (balance >= price) {
            moneyFunction(price);
            multiplierFunction(multiplier);
        } else {
            var newElement = ( // Stores div in variable to be stored in useStateArray
                <div className={styles.priceLossAnimation} style={{ top: Ylocation, left: Xlocation, fontSize: "30px", whiteSpace: "nowrap" }}>
                    Broke
                </div>
            );
            frontendArray(newElement)
            setTimeout(() => {
                // REMOVE ARRAY ITEMS HERE
            }, 3000);
        }
    };

    const upgradeBoxes = [{ title: "Upgrade 1", price: 0.25, multiplier: 2 }, { title: "Upgrade 2", price: 0.50, multiplier: 4 }, { title: "Upgrade 2", price: 0.50, multiplier: 4 }];
    const upgradeList = [upgradeBoxes.map((keyname) => {
        var priceChecker = balance >= keyname.price ? "#15e815" : "red";
        return (
            <Tippy content={"Price: " + keyname.price}>
                <div style={{
                    color: priceChecker,
                    border: "2px solid " + priceChecker,
                    "--backgroundColor": priceChecker,
                    "--backgroundColorGlow": priceChecker,
                }} className={styles.upgradeButton} onClick={(event) => clickCheck(event, keyname.price, keyname.multiplier)}>{keyname.title}</div>
            </Tippy>
        )
    })];
    return (
        <>
            {upgradeList}
        </>
    )
}
export default ClickUpgradesList;
